import { useEffect, useState } from 'react';
import useHttp from '../../hooks/use-http';
import classes from './Comments.module.css';
import NewCommentForm from './NewCommentForm';
import { useParams } from 'react-router-dom';
import { getAllComments } from '../../lib/api';
import LoadingSpinner from '../UI/LoadingSpinner';

const Comments = () => {
  const [isAddingComment, setIsAddingComment] = useState(false);
  const params=useParams();
  const {sendRequest,stauts,data:loadedComments,error}=useHttp(getAllComments,true)

  const {quoteId}=params;

  useEffect(()=>{
    sendRequest(quoteId)
  },[sendRequest,quoteId])

  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };

  const addCommentHandler=()=>{}

  let comment;

  if(stauts==='pending')
  {
    comment=<div className='centered'><LoadingSpinner></LoadingSpinner></div>
  }

  if(stauts==='completed'&&(loadedComments||loadedComments))

  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className='btn' onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && <NewCommentForm quoteId={quoteId} onAddComment={addCommentHandler}/>}
      <p>Comments...</p>
    </section>
  );
};

export default Comments;
