import { Route, useParams ,Link} from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from '../components/quotes/HighlightedQuote';

const DUMMY_QUOTES=[
    {id:'q1',author:'Max',text:'Learning React is Fun!'},
    {id:'q2',author:'july',text:'Learning Python is Fun!'},
]


const QuoteDetails = () => {
  const params = useParams();

    const quote = DUMMY_QUOTES.find(quotes => quotes.id === params.quoteId)

    if(!quote)
    {
        return <p>No quote Found.</p>
    }

  return (
    <section>
      <HighlightedQuote text={quote.text} author={quote.author}></HighlightedQuote>
      <Link to="/">Add Comment</Link>
      <Route path="/quotes/:quoteId/comment">
        <Comments></Comments>
      </Route>
    </section>
  );
};

export default QuoteDetails;
