import { Route, useParams, Link,useRouteMatch,useLocation } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
  { id: "q1", author: "Max", text: "Learning React is Fun!" },
  { id: "q2", author: "july", text: "Learning Python is Fun!" },
];

const QuoteDetails = () => {
  const match=useRouteMatch();
  const params = useParams();
  console.log
  console.log(match);
  const quote = DUMMY_QUOTES.find((quotes) => quotes.id === params.quoteId);

  if (!quote) {
    return <p>No quote Found.</p>;
  }

  return (
    <section>
      <HighlightedQuote
        text={quote.text}
        author={quote.author}
      ></HighlightedQuote>
      <Route path={`/quotes/${params.quoteId}`} exact>
        <div className="centered">
          <Link className="btn--flat" to={`/quotes/${params.quoteId}/comment`}>
            Load Comments
          </Link>
        </div>
      </Route>
      <Route path="/quotes/:quoteId/comment">
        <Comments></Comments>
      </Route>
    </section>
  );
};

export default QuoteDetails;
