import QuoteList from "../components/quotes/QuoteList";
import useHttp from "../hooks/use-http";
import {getAllQuotes} from "../lib/api"
import { useEffect } from "react";
import LoadingSpinner from "../components/UI/LoadingSpinner";

const DUMMY_QUOTES=[
    {id:'q1',author:'Max',text:'Learning React is Fun!'},
    {id:'q2',author:'july',text:'Learning Python is Fun!'},
]
    

const AllQuotes=()=>{

    const {sendRequest,status,data: loadedData,error}=useHttp(getAllQuotes,true);
    useEffect(()=>{
        sendRequest()
    },[sendRequest])

    if(status === 'pending')
    {
        return <div className="centered"><LoadingSpinner></LoadingSpinner></div>
    }

    if(error)
    {
        return <p className="centered focused">{error}</p>
    }

    if(status === '')

    return <QuoteList quotes={DUMMY_QUOTES}></QuoteList>
}

export default AllQuotes;
